import React, { useState } from 'react';
import styled from 'styled-components';

// Styled Components
const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  max-width: 400px;
  margin: 0 auto;
  border: 1px solid #ccc;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px;
  background-color: #fff;
  border-bottom: 1px solid #ccc;
  margin-bottom: 10px;
`;

const BotName = styled.h2`
  margin: 0;
  color: #007bff;
  font-size: 1.2rem;
`;

const LogoutButton = styled.button`
  padding: 8px 15px;
  border: none;
  border-radius: 5px;
  background-color: #dc3545;
  color: #fff;
  cursor: pointer;
  font-size: 0.9rem;

  &:hover {
    background-color: #c82333;
  }
`;

const MessagesContainer = styled.div`
  flex-grow: 1;
  overflow-y: auto;
  padding: 10px;
  margin: 0 20px;
`;

const Message = styled.div`
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 5px;
  background-color: ${props => props.isUser ? '#dcf8c6' : '#f0f0f0'};
  align-self: ${props => props.isUser ? 'flex-end' : 'flex-start'};
`;

const InputContainer = styled.div`
  display: flex;
  align-items: center;
  margin: 10px 20px;
  padding-top: 10px;
  border-top: 1px solid #ccc;
`;

const Input = styled.input`
  flex-grow: 1;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin-right: 10px;

  &:focus {
    outline: none;
    border-color: #007bff;
  }
`;

const SendButton = styled.button`
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  background-color: #007bff;
  color: #fff;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

function Chat({ setAuthenticated }) {
  const [messages, setMessages] = useState([
    { text: "Hello! How can I help you today?", isUser: false }
  ]);
  const [input, setInput] = useState('');
  const [cart, setCart] = useState([]);

  // Products data
  const products = {
    iphone: [
      { id: 1, name: "iPhone 15 Pro Max", price: 1099, specs: "256GB, Titanium", stock: 10 },
      { id: 2, name: "iPhone 15", price: 799, specs: "128GB, Multiple Colors", stock: 15 },
      { id: 3, name: "iPhone 14 Pro", price: 899, specs: "128GB, Space Black", stock: 8 },
      { id: 4, name: "iPhone 14", price: 699, specs: "128GB, Midnight", stock: 12 },
      { id: 5, name: "iPhone 13", price: 599, specs: "128GB, Multiple Colors", stock: 20 }
    ],
    // ... (keep all your other product categories) ...
  };

  const formatProduct = (product) => {
    return `${product.name} - $${product.price} (${product.specs}) [Stock: ${product.stock}]`;
  };

  const handlePurchase = (productId) => {
    const allProducts = Object.values(products).flat();
    const product = allProducts.find(p => p.id === productId);
    
    if (product && product.stock > 0) {
      setCart([...cart, product]);
      return `Great choice! I've added ${product.name} to your cart.\nWould you like to:\n1. Continue shopping\n2. View cart\n3. Proceed to checkout`;
    }
    return "Sorry, that product is out of stock.";
  };

  const viewCart = () => {
    if (cart.length === 0) {
      return "Your cart is empty.";
    }
    
    const total = cart.reduce((sum, item) => sum + item.price, 0);
    const cartItems = cart.map((item, index) => 
      `${index + 1}. ${item.name} - $${item.price} (${item.specs})`
    ).join('\n');

    return "Your Shopping Cart:\n" + 
           "------------------------\n" +
           cartItems +
           "\n------------------------" +
           `\nTotal: $${total}\n\n` +
           "What would you like to do?\n" +
           "1. Proceed to checkout\n" +
           "2. Continue shopping\n" +
           "3. Clear cart";
  };

  const checkout = () => {
    if (cart.length === 0) {
      return "Your cart is empty. Let me help you find some products!";
    }
    
    const total = cart.reduce((sum, item) => sum + item.price, 0);
    setCart([]); // Clear cart after checkout
    return `Thank you for your purchase!\nTotal paid: $${total}\n\nYour order will be processed and shipped soon.\nWould you like to continue shopping?`;
  };

  // ... (keep your getBotResponse function with all product categories) ...

  const handleSend = () => {
    if (input.trim() === '') return;

    const userMessage = { text: input, isUser: true };
    setMessages([...messages, userMessage]);

    setTimeout(() => {
      const botResponse = { text: getBotResponse(input), isUser: false };
      setMessages(prevMessages => [...prevMessages, botResponse]);
    }, 500);

    setInput('');
  };

  return (
    <ChatContainer>
      <Header>
        <BotName>Shopping Assistant Bot</BotName>
        <LogoutButton onClick={() => setAuthenticated(false)}>
          Logout
        </LogoutButton>
      </Header>
      <MessagesContainer>
        {messages.map((message, index) => (
          <Message key={index} isUser={message.isUser}>
            {message.text}
          </Message>
        ))}
      </MessagesContainer>
      <InputContainer>
        <Input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyPress={e => e.key === 'Enter' && handleSend()}
          placeholder="Type your message..."
        />
        <SendButton onClick={handleSend}>Send</SendButton>
      </InputContainer>
    </ChatContainer>
  );
}

export default Chat;
