import React, { useState } from 'react';
import styled from 'styled-components';

// ... (keep all your styled components the same) ...

function Chat({ setAuthenticated }) {
  const [messages, setMessages] = useState([
    { text: "Hello! How can I help you today?", isUser: false }
  ]);
  const [input, setInput] = useState('');

  const getBotResponse = (userMessage) => {
    const message = userMessage.toLowerCase();
    
    // Electronics related queries
    if (message.includes('electronics') || message.includes('laptop') || message.includes('phone') || message.includes('smartphone')) {
      return "We have a wide range of electronics! Here are some categories:\n" +
             "- Laptops (Dell, HP, Lenovo)\n" +
             "- Smartphones (iPhone, Samsung, Google)\n" +
             "- Accessories (Chargers, Cases, Cables)\n" +
             "What specific category interests you?";
    }
    
    // Price related queries
    if (message.includes('price') || message.includes('cost') || message.includes('how much')) {
      return "Our products are available in different price ranges:\n" +
             "- Budget phones: $200-$500\n" +
             "- Mid-range laptops: $500-$1000\n" +
             "- Premium devices: $1000+\n" +
             "What's your budget range?";
    }
    
    // Product queries
    if (message.includes('product') || message.includes('show') || message.includes('available')) {
      return "Here are our main product categories:\n" +
             "1. Electronics\n" +
             "2. Clothing\n" +
             "3. Home & Living\n" +
             "4. Books\n" +
             "Which category would you like to explore?";
    }
    
    // Help or general queries
    if (message.includes('help') || message.includes('assist') || message.includes('support')) {
      return "I can help you with:\n" +
             "- Finding products\n" +
             "- Checking prices\n" +
             "- Product recommendations\n" +
             "- Shopping assistance\n" +
             "What would you like help with?";
    }

    // Shopping assistance
    if (message.includes('buy') || message.includes('purchase') || message.includes('shopping')) {
      return "I'll help you make a purchase! To better assist you:\n" +
             "1. What type of product are you looking for?\n" +
             "2. Do you have a specific budget?\n" +
             "3. Any preferred brands?";
    }

    // Default response
    return "I understand you're interested in shopping. Could you please be more specific about what you're looking for? For example:\n" +
           "- Show me products\n" +
           "- I want to buy a laptop\n" +
           "- What's the price range for phones?";
  };

  const handleSend = () => {
    if (input.trim()) {
      // Add user message
      setMessages([...messages, { text: input, isUser: true }]);
      
      // Get and add bot response
      setTimeout(() => {
        const botResponse = getBotResponse(input);
        setMessages(prev => [...prev, { 
          text: botResponse, 
          isUser: false 
        }]);
      }, 1000);
      
      setInput('');
    }
  };

  return (
    <ChatContainer>
      <Header>
        <h2>Shopping Assistant</h2>
        <LogoutButton onClick={() => setAuthenticated(false)}>Logout</LogoutButton>
      </Header>
      
      <ChatArea>
        {messages.map((message, index) => (
          <MessageContainer key={index}>
            <Message isUser={message.isUser}>
              {message.text}
            </Message>
          </MessageContainer>
        ))}
      </ChatArea>
      
      <InputArea>
        <Input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Type your message..."
        />
        <Button onClick={handleSend}>Send</Button>
      </InputArea>
    </ChatContainer>
  );
}

export default Chat;
