import React, { useState } from 'react';
import styled from 'styled-components';

const ChatContainer = styled.div`
  width: 300px;
  height: 400px;
  border: 1px solid #ccc;
  border-radius: 10px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

const MessagesContainer = styled.div`
  flex: 1;
  padding: 10px;
  overflow-y: auto;
`;

const Message = styled.div`
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 10px;
  background-color: ${props => props.isUser ? '#e1f5fe' : '#fff3e0'};
  align-self: ${props => props.isUser ? 'flex-end' : 'flex-start'};
`;

const InputContainer = styled.div`
  padding: 10px;
  border-top: 1px solid #ccc;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 10px;
`;

const SendButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 10px;
  background-color: #4caf50;
  color: white;
  cursor: pointer;
`;

function Chat({ setAuthenticated }) {
  const [messages, setMessages] = useState([
    { text: "Hello! How can I help you today?", isUser: false }
  ]);
  const [input, setInput] = useState('');

  const products = {
    iphone: [
      "iPhone 15 Pro Max - $1099 (256GB, Titanium)",
      "iPhone 15 - $799 (128GB, Multiple Colors)",
      "iPhone 14 Pro - $899 (128GB, Space Black)",
      "iPhone 14 - $699 (128GB, Midnight)",
      "iPhone 13 - $599 (128GB, Multiple Colors)"
    ],
    samsung: [
      "Samsung Galaxy S23 Ultra - $1199 (256GB)",
      "Samsung Galaxy S23+ - $999 (128GB)",
      "Samsung Galaxy S23 - $799 (128GB)",
      "Samsung Galaxy A54 5G - $449",
      "Samsung Galaxy A34 5G - $399"
    ],
    dell: [
      "Dell XPS 13 - $999 (i5, 8GB RAM, 256GB SSD)",
      "Dell XPS 15 - $1499 (i7, 16GB RAM, 512GB SSD)",
      "Dell Inspiron 15 - $699 (i5, 8GB RAM, 256GB SSD)",
      "Dell G15 Gaming - $899 (i7, 16GB RAM, RTX 3050)",
      "Dell Latitude 5420 - $1299 (i7, 16GB RAM, 512GB SSD)"
    ],
    hp: [
      "HP Spectre x360 - $1299 (i7, 16GB RAM, 512GB SSD)",
      "HP Envy 13 - $899 (i5, 8GB RAM, 256GB SSD)",
      "HP Pavilion 15 - $649 (i5, 8GB RAM, 256GB SSD)",
      "HP Omen 16 - $1399 (i7, 16GB RAM, RTX 3060)",
      "HP EliteBook 840 - $1199 (i7, 16GB RAM, 512GB SSD)"
    ],
    lenovo: [
      "Lenovo ThinkPad X1 Carbon - $1399 (i7, 16GB RAM, 512GB SSD)",
      "Lenovo Yoga 9i - $1299 (i7, 16GB RAM, 512GB SSD)",
      "Lenovo IdeaPad 5 - $699 (i5, 8GB RAM, 256GB SSD)",
      "Lenovo Legion 5 - $999 (i7, 16GB RAM, RTX 3050Ti)",
      "Lenovo ThinkBook 14 - $899 (i5, 16GB RAM, 512GB SSD)"
    ],
    clothing: [
      "Men's T-Shirts - Starting from $19.99",
      "Women's Dresses - Starting from $29.99",
      "Jeans (All Brands) - Starting from $39.99",
      "Sports Wear - Starting from $24.99",
      "Winter Collection - Starting from $49.99"
    ],
    accessories: [
      "Phone Cases - Starting from $14.99",
      "Laptop Bags - Starting from $29.99",
      "Wireless Chargers - Starting from $19.99",
      "Screen Protectors - Starting from $9.99",
      "Power Banks - Starting from $24.99"
    ]
  };

  const getBotResponse = (userMessage) => {
    const message = userMessage.toLowerCase();

    // Specific product queries
    if (message.includes('iphone')) {
      return "Here are our available iPhone models:\n" + products.iphone.join('\n');
    }
    
    if (message.includes('samsung')) {
      return "Here are our available Samsung phones:\n" + products.samsung.join('\n');
    }
    
    if (message.includes('dell')) {
      return "Here are our available Dell laptops:\n" + products.dell.join('\n');
    }
    
    if (message.includes('hp')) {
      return "Here are our available HP laptops:\n" + products.hp.join('\n');
    }
    
    if (message.includes('lenovo')) {
      return "Here are our available Lenovo laptops:\n" + products.lenovo.join('\n');
    }

    // Electronics related queries
    if (message.includes('electronics') || message.includes('laptop') || message.includes('phone') || message.includes('smartphone')) {
      return "We have a wide range of electronics! Here are some categories:\n" +
             "- Laptops (Dell, HP, Lenovo)\n" +
             "- Smartphones (iPhone, Samsung)\n" +
             "- Accessories (Chargers, Cases, Cables)\n" +
             "Please specify which brand you're interested in (e.g., 'Show me Dell laptops' or 'Show me iPhones')";
    }
    
    // Clothing queries
    if (message.includes('cloth') || message.includes('wear') || message.includes('dress') || message.includes('fashion')) {
      return "Here's our clothing collection:\n" + products.clothing.join('\n');
    }

    // Accessories queries
    if (message.includes('accessor')) {
      return "Here are our available accessories:\n" + products.accessories.join('\n');
    }
    
    // Price related queries
    if (message.includes('price') || message.includes('cost') || message.includes('how much')) {
      return "Our products are available in different price ranges:\n" +
             "- Budget phones: $200-$500\n" +
             "- Mid-range laptops: $500-$1000\n" +
             "- Premium devices: $1000+\n" +
             "What's your budget range?";
    }
    
    // Product queries
    if (message.includes('product') || message.includes('show') || message.includes('available')) {
      return "Here are our main product categories:\n" +
             "1. Electronics\n" +
             "   - Laptops (Dell, HP, Lenovo)\n" +
             "   - Smartphones (iPhone, Samsung)\n" +
             "2. Clothing\n" +
             "3. Accessories\n" +
             "Which category would you like to explore?";
    }
    
    // Help or general queries
    if (message.includes('help') || message.includes('assist') || message.includes('support')) {
      return "I can help you with:\n" +
             "- Finding specific products (e.g., 'Show me iPhones')\n" +
             "- Checking prices (e.g., 'How much are Dell laptops?')\n" +
             "- Product recommendations\n" +
             "- Shopping assistance\n" +
             "What would you like help with?";
    }

    // Shopping assistance
    if (message.includes('buy') || message.includes('purchase') || message.includes('shopping')) {
      return "I'll help you make a purchase! To better assist you:\n" +
             "1. What type of product are you looking for?\n" +
             "2. Do you have a specific budget?\n" +
             "3. Any preferred brands? (We have Dell, HP, Lenovo, iPhone, Samsung)";
    }

    // Default response
    return "I understand you're interested in shopping. Could you please be more specific? Try:\n" +
           "- Show me iPhones\n" +
           "- Show me Dell laptops\n" +
           "- Show me accessories\n" +
           "- What's the price range for laptops?";
  };

  const handleSend = () => {
    const userMessage = input.trim();
    if (userMessage) {
      const botResponse = getBotResponse(userMessage);
      setMessages([...messages, { text: botResponse, isUser: false }]);
      setInput('');
    }
  };

  return (
    <ChatContainer>
      <MessagesContainer>
        {messages.map((message, index) => (
          <Message key={index} isUser={message.isUser}>
            {message.text}
          </Message>
        ))}
      </MessagesContainer>
      <InputContainer>
        <Input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <SendButton onClick={handleSend}>Send</SendButton>
      </InputContainer>
    </ChatContainer>
  );
}

export default Chat;
